import React, {Component} from 'react';
import OverHeadBar from "./OverHeadBar";
import detailPage from "./DetailPage.module.css";
import {ClickSizeButton, SizeButton} from "../Component/ClickButton";
import Button from "@material-ui/core/Button";

class DetailPage extends Component{
    state = {
        loading: true,
        details: null,
        picId: 0,
        size: 'S',
        /*sizeS: true,
        sizeM: false,
        sizeL: false,*/
        amount: 0
    };

    async componentDidMount() {
        const response = await fetch(`/api/products/${this.props.match.params.id}`);
        const product = await response.json();
        this.setState({loading: false, details: product})
        console.log(this.props);
        console.log(this.state.details.length);
    }


    decrement = () => {
        this.setState({
            amount: this.state.amount<=0? 0 : this.state.amount-1
        })
    }

    increment = () => {
        this.setState({
            amount: this.state.amount+1
        })
    }

    render() {
        if (this.state.loading) return <div>loading...</div>
        if (!this.state.details) return <div>no detail to display</div>
        const {name, description, price} = this.state.details[0];

        return (
            <div>
                <OverHeadBar currentPage='SHOP'/>
                <div className={detailPage.overview}>
                    <div className={detailPage.image}>
                        <div className={detailPage.left}>
                            {this.state.details.map((detail, index) => {
                            return (
                                <div>
                                    <img className={detailPage.smallpic}
                                         onClick={()=>{this.setState({picId: index})}}
                                         src={`/assets/${detail.picture}`}
                                         key={index+1}/>
                                </div>)})}
                        </div>
                        <div className={detailPage.right} >
                            <img className={detailPage.right}
                                 src={`/assets/${this.state.details[this.state.picId].picture}`} />
                        </div>
                    </div>
                    <div className={detailPage.text}>
                        <div className={detailPage.name}>{name}</div>
                        <div className={detailPage.description}>{description}</div>
                        <div className={detailPage.price}>{`£ ${price}`}</div>
                        <div className={detailPage.sizebox}>
                            <div className={this.state.size==='S'? detailPage.active : detailPage.sizebutton}>
                                {/*<Button className={this.state.sizeS? detailPage.active : detailPage.sizebutton}
                                        onClick={()=>this.setState({sizeS: true, sizeM: false, sizeL: false})}
                                        variant='contained'
                                        key='S'>
                                    S
                                </Button>
                                <Button className={this.state.sizeM? detailPage.active : detailPage.sizebutton}
                                        onClick={()=>this.setState({sizeS: false, sizeM: true, sizeL: false})}
                                        variant='contained'
                                        key='M'>
                                    M
                                </Button>*/}
                            <ClickSizeButton onClick={()=>this.setState({size: 'S'})}
                                             name='S'/>
                            </div>
                            <div className={this.state.size==='M'? detailPage.active : detailPage.sizebutton}>
                            <ClickSizeButton onClick={()=>this.setState({size: 'M'})}
                                             name='M'/>
                            <ClickSizeButton onClick={()=>this.setState({size: 'L'})}
                                             name='L'/>
                            </div>
                        </div>
                        <div className={detailPage.amountbox}>
                            <button className={detailPage.button} id="decrement" onClick={this.decrement}>-</button>

                            <input className={detailPage.amount} type="number" value={this.state.amount}/>
                            <button className={detailPage.button} id="increment" onClick={this.increment}>+</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}



export default DetailPage;
